import { React, useState } from "react";
import { ScrollView, Text, TextInput, View, Button ,StyleSheet} from "react-native";
import { Formik } from "formik";
import * as Yup from "yup";
import {
  Table,
  TableWrapper,
  Row,
  Rows,
  Col,
  Cols,
  Cell,
} from "react-native-table-component";

export default function ListTodo({ item }) {
  return (
    <ScrollView>
      <View style={styles.container}>
        <Table borderStyle={{ borderWidth: 2, borderColor: "#c8e1ff" }}>
          <Row data="Task" style={styles.head} textStyle={styles.text} />
          <Rows data={item.value} textStyle={styles.text} />
        </Table>
      </View>
      <View >
        <Text>{item.value}</Text>
        <Button title="Delete" />
        <Button title="Update" />
      </View>
    </ScrollView>
  );
}
const styles = StyleSheet.create({
  container: { flex: 1, padding: 16, paddingTop: 30, backgroundColor: "#fff" },
  head: { height: 40, backgroundColor: "#f1f8ff" },
  text: { margin: 6 },
});
